const options = {
  title: {
    display: true,
    text: "Most used hashtags",
  },
  legend: {
    display: false,
  },
};

function MostUsedHashtags() {
  const [first, setFirst] = useState(0);
  const [second, setSecond] = useState(0);
  const [third, setThird] = useState(0);
  const [fourth, setFourth] = useState(0);
  const [fifth, setFifth] = useState(0);

  useEffect(() => {
    const fetchData = async () => {
      await fetch("http://reagent1.f4.htw-berlin.de:8080/mostUsedHashtags/CDU")
        .then((response) => {
          return response.json();
        })
        .then((data) => {
          console.log("----------");
          console.log(data[4][2021][1]);
          console.log("----------");
          setFirst(data[4][2021][1]);
          setSecond(data[4][2021][2]);
          setThird(data[4][2021][3]);
          setFourth(data[4][2021][4]);
          setFifth(data[4][2021][5]);
        });
    };
    fetchData();
  }, []);

  return (
    <div className="charts">
      {
        <Bar
          data={{
            labels: [1, 2, 3, 4, 5],
            datasets: [
              {
                data: [first, second, third, fourth, fifth],
                backgroundColor: "rgba(54, 162, 235, 0.2)",
              },
            ],
          }}
          options={options}
        />
      }
    </div>
  );
}

export default MostUsedHashtags;
