import React, { useEffect, useState } from "react";
import { Bar, Line, Pie, Doughnut } from "react-chartjs-2";
import axios from "axios";

//const hour = [];
const spd = [];
const fdp = [];
const afd = [];
const linke = [];
const gruen = [];

function buildData(data) {
  const cdu = [];

  for (let d in data) {
    if (data[d]._id.party == "CDU") {
      let cdunew = {
        x: data[d]._id.hour,
        y: data[d].count,
      };
      cdu.push(cdunew);
    }
    /*if (data[d]._id.party == "SPD") {
      spd.push(data[d]);
    }
    if (data[d]._id.party == "FDP") {
      fdp.push(data[d]);
    }
    if (data[d]._id.party == "AfD") {
      afd.push(data[d]);
    }
    if (data[d]._id.party == "Linke") {
      linke.push(data[d]);
    }
    if (data[d]._id.party == "Grün") {
      gruen.push(data[d]);
    }*/
  }
  return cdu;
}
const t = [];

function Charts() {
  /*const [hour, setHour] = useState({});
  const [party, setParty] = useState({});
  const [count, setCount] = useState({});*/

  const [cduu, setCDUU] = useState({});
  const [hour, setHour] = useState({});
  const options = {
    responsive: true,
    labels: hour,
    tooltips: {
      mode: "label",
    },
    elements: {
      line: {
        fill: false,
      },
    },
    scales: {
      xAxes: [
        {
          display: true,
          gridLines: {
            display: false,
          },

          labels: hour,
        },
      ],
      yAxes: [
        {
          type: "linear",
          display: true,
          position: "left",
          id: "y-axis-1",
          gridLines: {
            display: false,
          },
          labels: {
            show: true,
          },
        },
        {
          type: "linear",
          display: true,
          position: "right",
          id: "y-axis-2",
          gridLines: {
            display: false,
          },
          labels: {
            show: true,
          },
        },
      ],
    },
  };

  useEffect(() => {
    const fetchData = async () => {
      await fetch("http://localhost:8080/jsonProcessed")
        .then((response) => {
          return response.json();
        })
        .then((data) => {
          //console.log(data);
          let chartdata = buildData(data);
          setCDUU(chartdata);
          console.log(chartdata);

          for (let d in data) {
            t.push(data[d]._id.hour);
          }
          setHour(t);
          //t.push(chartdata);
        });
    };
    fetchData();
  }, []);

  console.log(cduu);
  /*axios.get("http://localhost:8080/jsonProcessed").then((res) => {
    //console.log(res.data);
    for (let d in res.data) {
      if (res.data[d]._id.party == "CDU") {
        let cdunew = {
          x: res.data[d]._id.hour,
          y: res.data[d].count,
        };
        t.push(cdunew);
      }
    }
  });
  console.log(t);*/

  return (
    <div className="charts">
      {cduu?.length > 0 && (
        <Bar
          data={{
            datasets: [
              {
                label: "cdu",
                data: cduu,
              },
            ],
          }}
          //options={options}
        />
      )}
    </div>
  );
}
export default Charts;
