import React, { useEffect, useState } from "react";
import { Bar, Line, Pie, Doughnut } from "react-chartjs-2";
import axios from "axios";

//const hour = [];

function buildCDU(data) {
  const cdu = [];
  const spd = [];
  const fdp = [];
  const afd = [];
  const linke = [];
  const gruen = [];

  for (let d in data) {
    if (data[d]._id.party == "CDU") {
      let cdunew = {
        x: data[d]._id.hour,
        y: data[d].count,
      };
      cdu.push(cdunew);
    }
    /*if (data[d]._id.party == "SPD") {
      spd.push(data[d]);
    }
    if (data[d]._id.party == "FDP") {
      fdp.push(data[d]);
    }
    if (data[d]._id.party == "AfD") {
      afd.push(data[d]);
    }
    if (data[d]._id.party == "Linke") {
      linke.push(data[d]);
    }
    if (data[d]._id.party == "Grün") {
      gruen.push(data[d]);
    }*/
  }
  return cdu;
}
const t = [];

function Charts() {
  /*const [hour, setHour] = useState({});
  const [party, setParty] = useState({});
  const [count, setCount] = useState({});*/

  const [cduu, setCDUU] = useState({});
  const [hour, setHour] = useState({});
  const options = {
    responsive: true,
    labels: hour,
    tooltips: {
      mode: "label",
    },
    elements: {
      line: {
        fill: false,
      },
    },
    scales: {
      xAxes: [
        {
          display: true,
        },
      ],
      yAxes: [],
    },
  };

  useEffect(() => {
    const fetchData = async () => {
      await fetch("http://localhost:8080/jsonProcessed")
        .then((response) => {
          return response.json();
        })
        .then((data) => {
          //console.log(data);
          let chartdataCDU = buildCDU(data);
          setCDUU(chartdataCDU);

          for (let d in data) {
            t.push(data[d]._id.hour);
          }
          setHour(t);
          //t.push(chartdata);
        });
    };
    fetchData();
  }, []);

  console.log(cduu);
  /*axios.get("http://localhost:8080/jsonProcessed").then((res) => {
    //console.log(res.data);
    for (let d in res.data) {
      if (res.data[d]._id.party == "CDU") {
        let cdunew = {
          x: res.data[d]._id.hour,
          y: res.data[d].count,
        };
        t.push(cdunew);
      }
    }
  });
  console.log(t);*/

  return (
    <div className="charts">
      {cduu?.length > 0 && (
        <Bar
          data={{
            labels: [15, 16, 17, 18, 19, 20, 21, 22],
            datasets: [
              {
                label: "cdu",
                data: cduu,
              },
            ],
          }}
          options={options}
        />
      )}
    </div>
  );
}
export default Charts;
