  const [cduu, setCDUU] = useState({});
  useEffect(() => {
    const fetchData = async () => {
      await fetch("http://localhost:8080/jsonProcessed")
        .then((response) => {
          return response.json();
        })
        .then((data) => {
          //console.log(data);
          let chartdata = buildData(data);
          setCDUU(chartdata);
        });
    };
    fetchData();
  });

  //console.log(cduu);
