import React, { useEffect, useState } from "react";
import { Bar, Line, Pie, Doughnut } from "react-chartjs-2";
const options = {
  title: {
    display: true,
    text: "Total Tweets per Year",
  },
  legend: {
    display: true,
  },
};
function CountTotalByMounth() {
  const [cdu, setCDU] = useState(0);
  useEffect(() => {
    const fetchData = async () => {
      await fetch("http://reagent1.f4.htw-berlin.de:8080/countTweetByMonth")
        .then((response) => {
          return response.json();
        })
        .then((data) => {
          setCDU(data[0].CDU);
        });
    };
    fetchData();
  }, []);
  return (
    <div className="charts">
      {
        <Bar
          data={{
            labels: [2017, 2018, 2019, 2020, 2021],
            datasets: [
              {
                label: "CDU",
                data: [cdu[2017], cdu[2018], cdu[2019], cdu[2020], cdu[2021]],
                backgroundColor: "rgba(54, 162, 235, 0.4)",
              },
            ],
          }}
          options={options}
        />
      }
    </div>
  );
}

export default CountTotalByMounth;
