import React, { useEffect, useState } from "react";
import { Bar, Line, Pie, Doughnut } from "react-chartjs-2";
import numeral from "numeral";

function buildChartData(data) {
  const chartdata = [];
  chartdata.push(data[CDU][2017]);
  chartdata.push(data[CDU][2018]);
  chartdata.push(data[CDU][2019]);
  chartdata.push(data[CDU][2020]);
  chartdata.push(data[CDU][2021]);

  console.log(chartdata);
  return chartdata;
}

function AverageTweetLength() {
  const [cdu, setCDU] = useState(0);
  const [d, setD] = useState(0);
  useEffect(() => {
    const fetchData = async () => {
      await fetch("http://reagent1.f4.htw-berlin.de:9191/averagetweetlength")
        .then((response) => {
          return response.json();
        })
        .then((data) => {
          //console.log(data[CDU][2017]);
          setD(data);
        });
    };
    fetchData();
  }, []);
  return (
    <div className="charts">
      {
        <Bar
          data={{
            labels: [2017, 2018, 2019, 2020, 2021],
            datasets: [
              {
                label: "CDU",
                data: [
                  d[0][2017],
                  d[0][2018],
                  d[0][2019],
                  d[0][2020],
                  d[0][2021],
                ],
                backgroundColor: "rgba(54, 162, 235, 0.2)",
              } /*
              {
                label: "SPD",
                data: [
                  d[SPD][2017],
                  d[SPD][2018],
                  d[SPD][2019],
                  d[SPD][2020],
                  d[SPD][2021],
                ],
                backgroundColor: "rgba(54, 162, 235, 0.2)",
              },
              {
                label: "CDU",
                data: [
                  d[Linke][2017],
                  d[Linke][2018],
                  d[Linke][2019],
                  d[Linke][2020],
                  d[Linke][2021],
                ],
                backgroundColor: "rgba(54, 162, 235, 0.2)",
              },*/,
            ],
          }}
        />
      }
    </div>
  );
}

export default AverageTweetLength;
