import React, { useEffect, useState } from "react";
import { Bar, Line, Pie, Doughnut } from "react-chartjs-2";
import axios from "axios";

//const hour = [];
function buildSPD(data) {
  const spd = [];
  for (let d in data) {
    if (data[d]._id.party == "SPD") {
      let spdnew = {
        x: data[d]._id.hour,
        y: data[d].count,
      };
      spd.push(spdnew);
    }
  }
  return spd;
}
function buildCDU(data) {
  const cdu = [];
  const fdp = [];
  const afd = [];
  const linke = [];
  const gruen = [];

  for (let d in data) {
    if (data[d]._id.party == "CDU") {
      let cdunew = {
        x: data[d]._id.hour,
        y: data[d].count,
      };
      cdu.push(cdunew);
    }
  }
  return cdu;
}
