  const options = {
    responsive: true,
    tooltips: {
      mode: "label",
    },
    elements: {
      line: {
        fill: false,
      },
    },
    scales: {
      xAxes: [
        {
          display: true,
        },
      ],
      yAxes: [],
    },
  };

  useEffect(() => {
    const fetchData = async () => {
      await fetch("http://localhost:8080/jsonProcessed")
        .then((response) => {
          return response.json();
        })
        .then((data) => {
          //console.log(data);
          let chartdataCDU = buildCDU(data);
          setCDU(chartdataCDU);
          let chartdataSPD = buildSPD(data);
          setSPD(chartdataSPD);

