        let cdunew = {
          x: res.data[d]._id.hour,
          y: res.data[d].count,
        };
        t.push(cdunew);
      }
    }
  });
  console.log(t);*/

  return (
    <div className="charts">
      {cdu?.length > 0 && (
        <Bar
          data={{
            labels: [15, 16, 17, 18, 19, 20, 21, 22],
            datasets: [
              {
                label: "CDU",
                data: cdu,
              },
              {
                label: "SPD",
                data: spd,
              },
            ],
          }}
          options={options}
        />
      )}
    </div>
  );
}
export default Charts;
