import React, { useEffect, useState } from "react";
import { Bar, Line, Pie, Doughnut } from "react-chartjs-2";
const options = {
  title: {
    display: true,
    text: "Average Likes",
  },
  legend: {
    display: false,
  },
};
function AverageLikesTweets_cdu({ party }) {
  const [d, setD] = useState(0);
  useEffect(() => {
    const fetchData = async () => {
      await fetch("http://reagent1.f4.htw-berlin.de:8080/averagelikestweet")
        .then((response) => {
          return response.json();
        })
        .then((data) => {
          if (party == "cdu") {
            setD(data[0].CDU);
          } else if (party == "spd") {
            setD(data[5].SPD);
          } else if (party == "linke") {
            setD(data[1].Linke);
          } else if (party == "fdp") {
            setD(data[2].FDP);
          } else if (party == "afd") {
            setD(data[3].AfD);
          } else if (party == "gruen") {
            setD(data[4].B90);
          } else if (party == "csu") {
            setD(data[6].CSU);
          } else if (party == "parteilos") {
            setD(data[7].Parteilos);
          }
        });
    };
    fetchData();
  }, [party]);
  return (
    <div className="charts">
      {
        <Line
          data={{
            labels: [2017, 2018, 2019, 2020, 2021],
            datasets: [
              {
                fill: false,
                data: [cdu[2017], cdu[2018], cdu[2019], cdu[2020], cdu[2021]],
                backgroundColor: "rgba(54, 162, 235, 0.4)",
                borderColor: "rgba(54, 162, 235)",
              },
            ],
          }}
          options={options}
        />
      }
    </div>
  );
}

export default AverageLikesTweets_cdu;
