  let chartData = [];
  for (let d in data) {
    let newData = {
      text: Object.keys(data[d]),
      value: Object.values(data[d]),
    };
    console.log(newData[0]);
  }
}
function WordCloud() {
  const [d, setD] = useState(0);
  useEffect(() => {
    const fetchData = async () => {
      await fetch(
        "http://reagent1.f4.htw-berlin.de:8080/mostUsedHashtagsCloud/CDU"
      )
        .then((response) => {
          return response.json();
        })
        .then((data) => {
          //console.log(data[4]);
          buildData(data[4]);
        });
    };
    fetchData();
  }, []);
  return <div></div>;
}

export default WordCloud;
