import React, { useEffect, useState } from "react";
import { Bar, Line, Pie, Doughnut } from "react-chartjs-2";
import axios from "axios";

const hour = [];
const spd = [];
const fdp = [];
const afd = [];
const linke = [];
const gruen = [];
function buildData(data) {
  const cdu = [];

  for (let d in data) {
    if (data[d]._id.party == "CDU") {
      let cdunew = {
        x: data[d]._id_hours,
        y: data[d].count,
      };
      cdu.push(cdunew);
    }
    /*if (data[d]._id.party == "SPD") {
      spd.push(data[d]);
    }
    if (data[d]._id.party == "FDP") {
      fdp.push(data[d]);
    }
    if (data[d]._id.party == "AfD") {
      afd.push(data[d]);
    }
    if (data[d]._id.party == "Linke") {
      linke.push(data[d]);
    }
    if (data[d]._id.party == "Grün") {
      gruen.push(data[d]);
    }*/
  }
  return cdu;
}

function Charts() {
  /*const [hour, setHour] = useState({});
  const [party, setParty] = useState({});
  const [count, setCount] = useState({});*/

  const [cduu, setCDU] = useState({});

  useEffect(() => {
    const fetchData = () => {
      fetch("http://localhost:8080/jsonProcessed")
        .then((response) => {
          return response.json();
        })
        .then((data) => {
          //console.log(data);
          let chartdata = buildData(data);
          setCDU(chartdata);
        });
    };

    fetchData();
  });

  return (
    <div className="charts">
      <Bar
        data={{
          datasets: [
            {
              label: "cdu",
              data: [],
              backgroundColor: [
                "rgba(255, 99, 132, 0.2)",
                "rgba(54, 162, 235, 0.2)",
                "rgba(255, 206, 86, 0.2)",
                "rgba(75, 192, 192, 0.2)",
                "rgba(153, 102, 255, 0.2)",
                "rgba(255, 159, 64, 0.2)",
              ],
            },
          ],
        }}
        options={{
          maintainAspectRatio: false,
          scales: {
            y: {
              beginAtZero: true,
            },
          },
        }}
      />
    </div>
  );
}
export default Charts;
